// App component to grab user data and communicate to child components

import React from 'react';
import ReactDOM from 'react-dom';
import SeasonDisplay from './SeasonDisplay'

class App extends React.Component {

	//js specific for creating classes
	//automatically called props 
	constructor(props){
		//we have to call super due to extending React.Component
		super(props);

		//only time where you do direct assignment to state of the
		//component
		this.state = {lat: null};

		window.navigator.geolocation.getCurrentPosition(
			(position) => {
				this.setState({lat:position.coords.latitude});
			},
			(err) => console.log(err)
		);
	}

	//we have to define render
	render() {
		console.log("i was rendered")
		return <div>Latitude: {this.state.lat}</div>
	}
}

ReactDOM.render(
	<App />,
	document.querySelector('#root')
);
